REPLACE INTO `battlepay_display_info_locales` (`Id`, `Locale`, `Name1`, `Name2`, `Name3`, `Name4`) VALUES (1646, 'ruRU', '|cffe5cc80Desbloquear Apariencias', '', '|cff1eff00Desbloqueas Apariencias del arma de artefacto con todos tus personajes en la cuenta.', '');
REPLACE INTO `battlepay_display_info_locales` (`Id`, `Locale`, `Name1`, `Name2`, `Name3`, `Name4`) VALUES (1647, 'ruRU', '|cffe5cc80Desbloquear Aliado Elfo del Vacío', '', '|cff1eff00Alleria Brisaveloz ha descubierto que se exilió a un de elfos de sangre por estudiar magia del Vació. Si alguien encotrara a estos renegados, Alleria está convencida de que podría entrenarlos para convertirlos en arma de la Alianza.', '');
REPLACE INTO `battlepay_display_info_locales` (`Id`, `Locale`, `Name1`, `Name2`, `Name3`, `Name4`) VALUES (1648, 'ruRU', '|cffe5cc80Desbloquear Aliado Draenei forjado por la Luz', '', '|cff1eff00El Ejército de la Luz desempeñó un papel fundamental en la victoria contra la Legion Ardiente en Argus. El alto exarca Turalyon ha sugerido que estos soldados forjados por la Luz defenderían con orgullo a la Alianza de sus enemigos.', '');
REPLACE INTO `battlepay_display_info_locales` (`Id`, `Locale`, `Name1`, `Name2`, `Name3`, `Name4`) VALUES (1649, 'ruRU', '|cffe5cc80Desbloquear Aliado Elfos Nocheterna', '', '|cff1eff00Tras miles de años de aislamiento, los Nocheterna de Suramar anhelan volver a formar parte de este mundo. Lady Líadrin cree que la Horda se Beneficiaría de una alianza con estos elfos tan antiguos.', '');
REPLACE INTO `battlepay_display_info_locales` (`Id`, `Locale`, `Name1`, `Name2`, `Name3`, `Name4`) VALUES (1650, 'ruRU', '|cffe5cc80Desbloquear Aliado Tauren Monte Alto', '', '|cff1eff00Las tribus tauren de Monte Alto se unieron para expulsar a la Legion Ardiente de sus tierras. Baine Pezuña de Sangre cree que su fuerza y su nobleza serían una gran oportación para la Horda.', '');
